        de: 'de-DE',
        it: 'it-IT',
        jp: 'ja-JP',
        ko: 'ko-KR'
    };
    if (!defaultFullLanguageCodes[args.voice.languageCode])
        return 'en-US';
    return defaultFullLanguageCodes[args.voice.languageCode];
})()
